mapper.insert:
INSERT INTO SCHOOL_LIST
   (
       SCHOOL_SEQ
      ,SCHOOL_NAME
      ,SCHOOL_ADDR
   )
VALUES
   (
       school_seq.nextval
      ,#{school_name}
      ,#{school_addr}
   )

mapper.select:
SELECT SCHOOL_SEQ
FROM SCHOOL_LIST
WHERE SCHOOL_NAME = #{school_name} AND SCHOOL_ADDR = #{school_addr}
