mapper.insert:
INSERT INTO STUDYBOOK(
		BOOK_SEQ
		,MEMBER_SEQ
		,SCHOOL_SEQ
		,BOOK_NAME
		,BOOK_CONTENT
		,BOOK_IMG_LASTNUMBER
		,BOOK_FOLDER_NUMBER)
VALUES
	(	studybook_seq.nextval
		,#{member_seq}
		,#{school_seq}
		,#{book_name}
		,#{book_content}
		,#{book_img_lastnumber}
		,#{book_folder_number})

mapper.select:
SELECT * FROM STUDYBOOK
WHERE SCHOOL_SEQ = #{school_seq}
ORDER BY BOOK_SEQ ASC
